package kasiskimethod;

import java.util.ArrayList;

/**
 *
 * @author Jose Manuel Martínez de la Insua 
 */
public class KasiskiMethod {
    
    /**
     * @param args the command line arguments
     */
    public static void main(String[] args) {
        
        final String text_1 = "";
        final String text_2 = "";
        final String text_3 = "";
        
        // Probamos a descifrar el texto numero 2.
        String cipher_text = text_2;
        
        // keywords
        String[] keywords = {"SOBRE","ENTRE","CUANDO","TAMBIEN","PORQUE" ,
                             "MISMO","HOMBRE", "AUNQUE", "SIEMPRE","DESPUES",
                             "DESDE", "DURANTE" ,"TIEMPO", "DONDE", "EMBARGO",
                             "MIENTRAS", "ENTONCES","HASTA","MOMENTO","HABIA"};
        
        // Instanciacion del objeto tipo StringTools para trabajar con cadenas.
        StrTools strtools = new StrTools();
        
        // Instanciacion del objeto Kasiski.
        Kasiski k = new Kasiski(cipher_text);
        
        // Chequeamos N- gramas para deducir periodo.
        System.out.println("\nCheckeando N-gramas: \n");
        for(int i = 20; i > 2; i--){
            System.out.println(i+"-grama: ");
            // En cuanto localiza la secuencia de N-gramas para de calcular.
            if(strtools.ngrama(cipher_text,i)) break;
        } 
        
        // Calculamos el periodo por el Indice de Coincidencia 
        int period = strtools.period_by_ic(cipher_text);
        

        System.out.println("\nLongitud de la clave : " + period + "\n");

        System.out.println("\nGenerando lista de claves candidatas.");
        System.out.println("Paciencia, puede tardar unos minutos...\n");
        
        ArrayList<String> keylist = k.key_gen( period , 5);
        
        System.out.println("\nGenerada lista claves candidatas.\n");
        
        // Memoria Caché  
        String tmp;
        int count;
        
        
        System.out.println("\nCalculando claves.");
        System.out.println("Paciencia, puede tardar unos minutos...\n");
        
        
        for(String s: keylist){
            tmp = k.decrypt(s);
            count = 0;
            for (String keyword : keywords) {
                if (tmp.contains(keyword)) 
                    count++;
            }
            if(count>=8){
                System.out.println("ENCONTRADA CLAVE! : " + s);
                System.out.println("Texto descifrado con clave: " + s);
                System.out.println(tmp + "\n");
                System.exit(0);
            }else if(count>=4 && count<8){
                System.out.println("Encontrada candidata a clave : " + s);
                System.out.println("Texto descifrado con clave: " + s);
                System.out.println(tmp + "\n");
            }
            
        }
        
        
    }
    
}
